 */
class News extends BaseNews
{
  public function save(Doctrine_Connection $conn = null)
  {
    if (!$this->isNew())
    {
      $this->setUpdatedAt(date('Y-m-d h:i:s'));
    }

    return parent::save($conn);
  }

  public function getStatusName()
  {
    return $this->status ? 'Неактивно' : 'Активно';
  }

}
